package com.ipd.testjsf.example;

import java.util.concurrent.SynchronousQueue;
import java.util.concurrent.ThreadPoolExecutor;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicLong;

import com.ipd.testjsf.HelloService;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.context.support.ClassPathXmlApplicationContext;


/**
 * Title: <br>
 * <p/>
 * Description: <br>
 * <p/>
 */
public class ExampleMultiThreadClientMain {

    private final static Logger LOGGER = LoggerFactory.getLogger(ExampleMultiThreadClientMain.class);


    public static void main(String[] args) throws InterruptedException {
        ClassPathXmlApplicationContext appContext = new ClassPathXmlApplicationContext(
                "/jsf-consumer-example.xml");
        final HelloService service = (HelloService) appContext.getBean("helloService");
        LOGGER.info("service:" + service);

        final int threads = 30;
        final AtomicLong cnt = new AtomicLong(0);
        final ThreadPoolExecutor service1 = new ThreadPoolExecutor(threads, threads, 0L, TimeUnit.MILLISECONDS, new SynchronousQueue<Runnable>());// 无队列
        for (int i = 0; i < threads; i++) {
        	service1.execute(new Runnable() {
                @Override
                public void run() {
                    int n = 0;
                    while (true) {
                        try {
                            // 1k
                            service.echoStr("");
                            // 5k
                            //service.echoStr("");
                            cnt.incrementAndGet();
                        } catch (Exception e) {
                            LOGGER.error("",e );
                        }
                    }
                }
            });
        }

        Thread thread = new Thread(new Runnable() {
            private long last = 0;
            @Override
            public void run() {
                while (true) {
                    long count = cnt.get();
                    long tps = count - last;
                    LOGGER.info("last 1s invoke: {}, queue: {}", tps, service1.getQueue().size());
                    last = count;

                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException e) {
                    }
                }
            }
        },"Print-tps-THREAD");
        thread.start();

        // JSFContext.destroy();
    }
}